window.addEventListener("load",()=>{const e=document.querySelector(".nav__burger-btn"),t=document.querySelector(".nav__menu");e.addEventListener("click",()=>{e.classList.toggle("burger-active"),t.classList.toggle("nav__menu-active")}),t.addEventListener("click",e=>{setTimeout(()=>{e.target.classList.contains("nav__link")&&t.classList.remove("nav__menu-active")},300)})});