window.addEventListener("load",()=>{const e=document.querySelector(".nav__burger"),t=document.querySelector(".nav__menu");e.addEventListener("click",()=>{e.classList.toggle("burger-active"),t.classList.toggle("nav__menu-active")}),t.addEventListener("click",n=>{setTimeout(()=>{n.target.classList.contains("nav__link")&&(t.classList.remove("nav__menu-active"),e.classList.remove("burger-active"))},300)})});