window.addEventListener("load",()=>{const e=document.querySelector(".nav");window.addEventListener("scroll",()=>{e.classList.toggle("nav-active",scrollY>0)});const t=document.querySelector(".nav__burger"),n=document.querySelector(".nav__menu");t.addEventListener("click",()=>{t.classList.toggle("burger-active"),n.classList.toggle("nav__menu-active")}),n.addEventListener("click",e=>{setTimeout(()=>{e.target.classList.contains("nav__link")&&(n.classList.remove("nav__menu-active"),t.classList.remove("burger-active"))},300)})});