window.addEventListener("load",()=>{const e=e=>document.querySelector(e);e(".nav").addEventListener("click",e=>{e.target.classList.contains("nav__menu-item")&&(t.classList.remove("burger-active"),n.classList.remove("nav__menu-active"))});const t=e(".nav__burger"),n=e(".nav__menu");t.addEventListener("click",()=>{t.classList.toggle("burger-active"),n.classList.toggle("nav__menu-active")})});